import React, { Component } from 'react';
import './Card.css';
import Modal from 'react-responsive-modal';

class Card extends Component {
  constructor(props) {
    super(props);

    // this.state.text = this.props.text;
    this.state = {
      open: false,
      buttonX: null,
      buttonY: null,
      styles: null
    };
  }

  componentDidMount() {
    this.setState({
      buttonX: this.refs.button.left,
      buttonY: this.refs.button.top
    });
    console.log(this.state.buttonX);
    console.log(this.state.buttonY);
  }

  onOpenModal = () => {
    this.setState({
      open: true,
      styles: {
        backgroundColor: red
      }
    });
  }

  onCloseModal = (event) => {
    if (event.target.baseVal) {
      this.setState({
        open:false,
        styles: {
          display: none
        }
      });
    }
  }

  changeColor = (button) => {
    button.style.background = "#FFDAB9";
    button.style.borderColor = "#FFDAB9";
  }

  // getButtonPos = (button) => {
  //   this.setState({
  //     buttonX: button.left,
  //     buttonY: button.top
  //   });
  // }

  // () => {this.onOpenModal(); this.changeModalPos.bind(this,"modal1")}

  // <div className="modal" id={this.props.modalId} style={this.state.styles}>
  //   <button type="button" style={this.state.styles} onClick={this.onCloseModal} className="close" data-dismiss="modal">&times;</button>
  //   <h2>Simple centered modal</h2>
  // </div>

  render() {
    return (
      <div className="Context">
        <button ref="button" onClick={(event) => {this.onOpenModal(); this.changeColor(event.target);}}>{this.props.children}</button>
        <Modal ref="modal1" open={this.state.open} onClose={(event) => {this.onCloseModal(event.target)}} center>
          <h2>Simple centered modal</h2>
        </Modal>

      </div>
    );
  }
}

export default Card;
