import React, { Component } from 'react';
import Modal from 'react-responsive-modal';
import './Card.css';

class Card extends Component {
  constructor(props) {
    super(props);

    // this.state.text = this.props.text;
    this.state = {
      open: false
    };
  }

  onOpenModal = () => {
    this.setState({ open: true });
  }

  onCloseModal = (event) => {
    console.log(event.className);
    if (event.target) {
      if (event.target.className === "styles_closeIcon__1QwbI") {
        this.setState({ open: false });
      }
    }

  };

  changeColor = (button) => {
    button.style.background = "#FFDAB9";
    button.style.borderColor = "#FFDAB9";
  }

  render() {
    return (
      <div className="Context">
        <button onClick={(event) => {this.onOpenModal(); this.changeColor(event.target)}}>{this.props.children}</button>
        <Modal open={this.state.open} onClose={(event) => {this.onCloseModal(event.target)}} center>
          <h2>Simple centered modal</h2>
        </Modal>
      </div>
    );
  }
}

export default Card;
